import React from "react";
import {
  InfoWindow,
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker,
} from "react-google-maps";
import Geocode from "react-geocode";
import AutoComplete from 'react-google-autocomplete';
import { Grid } from '@material-ui/core';
import youtube from './youtube';
import SearchBar from './SearchBar';
import VideoList from './VideoList';
import styled from "styled-components"
import VideoDetail from './VideoDetail';
import Dashboard from "./Dashboard";
import zIndex from "@material-ui/core/styles/zIndex";
import { height } from "@mui/system";
import Footer from "./Footer";
import { Player } from 'video-react';
import ReactPlayer from 'react-player'

Geocode.setApiKey("AIzaSyCGX39_vj1YuXzup9jOmR29Iw_u_5Y4JQM")



class Map extends React.Component{


        state = {


          videos:[],
          // selectedVideo: null,
          foodvideos:[],
          // foodselectedVideo: null,
          workvideos:[],
          // workselectedVideo: null,

          dhksc: "",

          address :"",
          city :"",
          area :"",
          state :"",
          zoom :"15",
          height :"400",
          mapPosition : {
            lat:0,
            lng:0,
          },
          markerPosition :{
            lat:0,
            lng:0,
          },
        }



        componentDidMount() {
          if(navigator.geolocation) {
            navigator.geolocation.getCurrentPosition( position => {
              this.setState({
                mapPosition :{
                  lat: position.coords.latitude,
                  lng: position.coords.longitude,
                },
                markerPosition: {
                  lat:position.coords.latitude,
                  lng:position.coords.longitude,
                }
              },() => {
                Geocode.fromLatLng(position.coords.latitude,position.coords.longitude)
                .then(response => {

                 const address = response.results[0].formatted_address,
                       addressArray = response.results[0].address_components,
                       city = this.getCity(addressArray),
                       area = this.getArea(addressArray),
                       state = this.getState(addressArray);

                   this.setState({
                     address : (address) ? address : "",
                     city : (city) ? city: "",
                     area : (area) ? area: "",
                     state: (state)? state: "",
              })
            })
          })
        })
       }


      }


      // componentDidMount(){
      //   this.handleSubmit("stockholm")
      //   this.WorkhandleSubmit("stockholm")
      //   this.FoodhandleSubmit("stockholm")
      // }


        getCity = (addressArray) => {
          let city = '';
          for(let index=0; index<addressArray.length; index++) {
            if(addressArray[index].types[0]&&'administrative_area_level_2' === addressArray[index].types[0]) {
             city=addressArray[index].long_name;
             return city;
            }
          }
        }

      getArea=(addressArray) => {
        let area = '';
        for(let index=0; index<addressArray.length; index++) {
          if(addressArray[index].types[0]){
            for(let j =0; j<addressArray.length; j++) {
              if('sublocalty_level_1' === addressArray[index].types[j] || 'localty' ===addressArray[index].types[j]) {
            area = addressArray[index].long_name;
            return area;
            }
           }
          }
         }
        }

      getState =(addressArray) => {
        let state = '';
        for(let index=0; index<addressArray.length; index++) {
          for(let index=0; index<addressArray.length; index++) {
            if(addressArray[index].types[0] && 'administrative_area_level_1' ===addressArray[index].types[0]) {
              state = addressArray[index].long_name;
              return state;
            }
          }
        }
      }


        onMarkerDragEnd= (event) => {
          let newLat = event.latLng.lat();
          let newLng = event.latLng.lng();


          Geocode.fromLatLng(newLat,newLng)
          .then(response => {
           console.log('response', response)
           const address = response.results[0].formatted_address,
                 addressArray = response.results[0].address_components,
                 city = this.getCity(addressArray),
                 area = this.getArea(addressArray),
                 state = this.getState(addressArray);

             this.setState({
               address : (address) ? address : "",
               city : (city) ? city: "",
               area : (area) ? area: "",
               state: (state)? state: "",
               markerPosition: {
                 lat:newLat,
                 lng:newLng
               },
               mapPosition: {
                 lat:newLat,
                 lng:newLng
               }
             })
          })
       }



    handleSubmit = async (termFromSearchBar) => {
        const response = await youtube.get('/search', {
            params: {
                q: termFromSearchBar
            }
        })

        this.setState({
          videos: response.data.items,
          // selectedVideo:response.data.items[0] 
        });
        console.log("this is resp",response);
    };


    FoodhandleSubmit = async (termFromSearchBar) => {
      const response = await youtube.get('/search', {
          params: {
              q: 'food'+termFromSearchBar
          }
      })

      this.setState({
        foodvideos: response.data.items,
        // foodselectedVideo:response.data.items[0] 
      });
      console.log("this is resp",response);
  };


  WorkhandleSubmit = async (termFromSearchBar) => {
    const response = await youtube.get('/search', {
        params: {
            q: 'work'+termFromSearchBar
        }
    })

    this.setState({
      workvideos: response.data.items,
      // workselectedVideo:response.data.items[0] 
    });
    console.log("this is resp",response);
};





       onPlaceSelected = (place) => {
        const address = place.formatted_address,
              addressArray = place.address_components,
               // plus
              dhksc = place.address_components[0].long_name,

              city = this.getCity(addressArray),
              area = this.getArea(addressArray),
              state = this.getState(addressArray),
              newLat = place.geometry.location.lat(),
              newLng = place.geometry.location.lng();


      //   const handleSubmit = async (termFromSearchBar) => {
      //     const response = await youtube.get('/search', {
      //         params: {
      //             q: termFromSearchBar
      //         }
      //     })

      //     this.setState({
      //       videos: response.data.items,
      //       selectedVideo:response.data.items[0] 
      //     });
      //     console.log("this is resp",response);
      // };


          // Set these values in the state.
          this.setState({
            address: (address) ? address : '',
            area: (area) ? area : '',
            city: (city) ? city : '',
            state: (state) ? state : '',
            dhksc: (dhksc) ? dhksc : '', 
         　
            // plus
              markerPosition: {　
                  lat: newLat,
                  lng: newLng
              },
              mapPosition: {
                  lat: newLat,
                  lng: newLng
              },
          })

          this.handleSubmit(dhksc)
          this.FoodhandleSubmit(dhksc)
          this.WorkhandleSubmit(dhksc)
          console.log(place)
          console.log(dhksc)

       }


    //    handleVideoSelect = (video) => {
    //     this.setState({
    //         selectedVideo: video
    //     })
    // }




    render(){
              const MapWithAMarker = withScriptjs(withGoogleMap(props =>
                <GoogleMap
                  defaultZoom={8}
                  defaultCenter={{ lat: this.state.mapPosition.lat , lng: this.state.mapPosition.lng }}

                >

                  <Marker
                    draggable={true}
                    onDragEnd={this.onMarkerDragEnd}
                    position={{ lat: this.state.markerPosition.lat , lng: this.state.markerPosition.lng}}
                  >
                    <InfoWindow>
                      <div>
                       {this.state.address}
                      </div>
                   </InfoWindow>
                  </Marker>



                  {/* <Search> */}


                  {/* </Search> */}
                </GoogleMap>
              ));


        return(
         <div>

          <MapWrapper>
            <MapWrapper1>
              <PlayerWrapper>
                <ReactPlayer 
                url="https://assets.mixkit.co/videos/preview/mixkit-venice-central-canal-at-night-4646-large.mp4"
                playing="true"
                loop="true"
                width='100%'
                height='100%'
                />
              </PlayerWrapper> 
              <MapInner>
                  <SearchText>
                    <p>You can start your journey with three category of city you want to go.</p>
                  </SearchText>
                  <AutoComplete
                        types={['(country)']}
                        onPlaceSelected= {this.onPlaceSelected}
                        style={{
                          width: '100%',
                          height: '40px',
                          paddingLeft: '16px',
                          marginTop: '2px',
                          marginBottom: '2rem',
                        }}
                  /> 
              </MapInner>
            </MapWrapper1>



            <MapWrapper2>
              <MapWithAMarker
                        googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyCGX39_vj1YuXzup9jOmR29Iw_u_5Y4JQM&v=3.exp&libraries=geometry,drawing,places"
                        loadingElement={<div style={{ height: `100%` }} />}
                        containerElement={<div style={{ height: `50vh` }} />}
                        mapElement={<div style={{ height: `100%` }} />}
                />
            </MapWrapper2>
          </MapWrapper>



         <TopCitywrapper>
         　<DivTopCitywrapper>
            <Div2TopCitywrapper>             
              <h1>City</h1>          
            </Div2TopCitywrapper> 
           </DivTopCitywrapper> 
         </TopCitywrapper>


         <TopCitywrapper>
         　<DivSecondCitywrapper>
            <Div2SecondCitywrapper>
              <p>The cityscape remains in your heart forever.  </p>
              <Border></Border>
            </Div2SecondCitywrapper> 
           </DivSecondCitywrapper> 
         </TopCitywrapper>




         <VideoWrapper>

          <VideoImage>
            <VideoImage1>
             <h3>City</h3>
             <VideoImage2>
              <img src="https://images.unsplash.com/photo-1523304867125-2293c498e08a?ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mzh8fGxvbmRvbnxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=60" height="440vh" width="330vw" />
             </VideoImage2>
            </VideoImage1>
          </VideoImage> 

          <VideoItems>
           <VideoItem>
            <VideoList handleVideoSelect={this.handleVideoSelect} videos={this.state.videos} />
           </VideoItem>
          </VideoItems>

         </VideoWrapper>


         <TopCitywrapper>
         　<DivTopCitywrapper>
            <Div2TopCitywrapper>             
              <h1>Work</h1>          
            </Div2TopCitywrapper> 
           </DivTopCitywrapper> 
         </TopCitywrapper>


         <TopCitywrapper>
         　<DivSecondCitywrapper1>
            <Div2SecondCitywrapper>
            <p>Working make someones's life more exiting. </p>
              <Border></Border>
            </Div2SecondCitywrapper> 
           </DivSecondCitywrapper1> 
         </TopCitywrapper>
　　　　　　

         <VideoWrapper>
          <VideoItems>
            <VideoItem>
             <VideoList handleVideoSelect={this.handleVideoSelect} videos={this.state.workvideos} />
            </VideoItem>
          </VideoItems>
          <VideoImage>
            <VideoImage1>
            <h3>Work</h3>
            <VideoImage2>
              <img src="https://images.unsplash.com/photo-1517971129774-8a2b38fa128e?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MjIyfHx3b3JraW5nfGVufDB8fDB8fA%3D%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=60" height="440vh" width="330vw" />
            </VideoImage2>
            </VideoImage1>
          </VideoImage>
　　　　　　</VideoWrapper>



          <TopCitywrapper>
         　<DivTopCitywrapper>
            <Div2TopCitywrapper>             
              <h1>Food</h1>          
            </Div2TopCitywrapper> 
           </DivTopCitywrapper> 
          </TopCitywrapper>


          <TopCitywrapper>
          　<DivSecondCitywrapper>
              <Div2SecondCitywrapper>
                <p>To eat is to color your life.</p>
                <Border></Border>
              </Div2SecondCitywrapper> 
            </DivSecondCitywrapper> 
          </TopCitywrapper>



          <VideoWrapper>

            <VideoImage>
              <VideoImage1>
                <h3>Food</h3>
              <VideoImage2>
                <img src="https://images.unsplash.com/photo-1579113800032-c38bd7635818?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MTEwfHxmb29kfGVufDB8fDB8fA%3D%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=60" height="440vh" width="330vw" />
              </VideoImage2>
                <p>Cityscape is one of the important elements for living there.
                  You can feel the city you find here. </p>
              </VideoImage1>
            </VideoImage>

            <VideoItems>
              <VideoItem>
               <VideoList handleVideoSelect={this.handleVideoSelect} videos={this.state.foodvideos} />
              </VideoItem>
            </VideoItems>
  　　　　　　</VideoWrapper>


            <FooterCaption>
              <FooterCaption1>
              <p>you can find some Instagram  post of #city name you research</p>
              </FooterCaption1>
            </FooterCaption>


             <Footer/>



         </div>

        );
    }
}

