import React,{useState,useEffect} from 'react';
import styled from "styled-components"



function Footer() {

    const [Post,setPost] = useState([]);
    const [Caption,setCaption] = useState([]);
    const [Post1,setPost1] = useState([]);
    const [Caption1,setCaption1] = useState([]);
    const [Post2,setPost2] = useState([]);
    const [Caption2,setCaption2] = useState([]);


    useEffect(() => {
        fetch("https://graph.facebook.com/17841564013123618/top_media?user_id=17841411766795081&fields=id,media_url,caption,permalink,media_type,comments_count,like_count&access_token=EAA9mxzmBApoBADIphJmrtMpAtjM5tZCplHlnga8ZBkDcfNuTOCA5aZBZAOwZCMJUOx9ZAZB4qNDxIXK0a0SlWzupWbgRSlEPSZAehdfcADL6GEB4U8STGVM0tNkO1WuZCF2bZAqXVG7YgyZC4vwNW5guTnBclKcLbb6jcTbfMhWZC4g07rJmA0ZBiUc6ZA")
          .then((response) => response.json())


          .then((response) => {
            console.log(response)
            console.log(response.data[0])
            setPost(response.data[0].media_url);
            setCaption(response.data[0].caption);
            setPost1(response.data[1].media_url);
            setCaption1(response.data[1].caption);
            setPost2(response.data[2].media_url);
            setCaption2(response.data[2].caption);
          });

        //   .then((data) => {
        //         const image = data.caption
        //         const caption = data[0]
        //       });

      }, 




      []);



    return(
        <FooterContent>
            <FooterContent1>
              <FooterImage>
                  <img src={Post} height="150vh" width="100vw"/>
              </FooterImage>
                <p>{Caption}</p>
            </FooterContent1>

            <FooterContent2>
              <FooterImage>
                  <img src={Post1} height="150vh" width="100vw"/>
              </FooterImage>
                <p>{Caption1}</p>
            </FooterContent2>

            <FooterContent3>
              <FooterImage>
                  <img src={Post2} height="150vh" width="100vw"/>
              </FooterImage>
                <p>{Caption2}</p>
            </FooterContent3>
        </FooterContent>



    )
}


export default Footer;

const FooterContent = styled.div`

margin-left: 32px;
margin-right: 32px;
display: flex;
`

const FooterContent1　 = styled.div`
width: 33.333%;
margin-right: 12px;
margin-left: 12px;

`

const FooterContent2= styled(FooterContent1)`

`

const FooterContent3= styled(FooterContent1)`

`

const FooterImage　 = styled.div`
object-fit: contain;
height: 100%;

`
