import axios from "axios"
import { useEffect, useState ,useContext} from "react"
import { useParams } from "react-router"
import { API } from "../api"
import {useHistory, NavLink} from 'react-router-dom';
import { AuthContext } from "../contexts/AuthContext"
import Grid from '@mui/material/Grid';
import styled from "styled-components"

export function CountryDetail() {
    const [country, setCountry] = useState(null)
    const { id } = useParams()
    const { user: { token } } = useContext(AuthContext)

    useEffect(() => {
      function  fetchCountry() {
        axios.get(API.countries.retrieve(id),{
          headers: {
            "Authorization": `Token ${token}`
        }
        })
          .then(res => {
            console.log(res.data)
            setCountry(res.data)
          })
      }
      fetchCountry()
      return () => null
    }, [id, token])

    return (

      <Detail>
      {country && (
      <Grid container spacing={2} style={{height: "80vh"}}>
        <Grid item xs={5} >
         {country.country_image && (
               <CountryImg>
                   <img src={country.country_image}  alt={country.country_image} height="auto" width="100%" object-fit="contain"/>
               </CountryImg>     
          )}
        </Grid>
        <Grid item xs={7}>
         {country.country_name}
         {country.content}
        </Grid>
      </Grid>
      )}
      </Detail>


            //   {country.is_owner && (
            //   <div>
            //   <NavLink to={`/countries/${country.id}/update`}>
            //  Update
            //   </NavLink>
            //   <NavLink to={`/countries/${country.id}/delete`}>
            //  Delete
            //   </NavLink>
            //   </div>
            //   )}





    )
}

export default CountryDetail


const Detail = styled.div`

margin-top: 50vh;
height: 100vh;

`

const CountryImg = styled.div`

padding: 10% ;
height: 100%;
`
